import React, { useState, useEffect } from 'react';
import './App.css';

//4.1 *********************************************************
//import React, { useState } from 'react';
import './App.css';
import peliculas from './data/peliculas.json';
// ******************************************************************

function App() {
  // Estados del componente
  const [peliculas, setPeliculas] = useState([]);
  const [peliculasFiltradas, setPeliculasFiltradas] = useState([]);
  const [busqueda, setBusqueda] = useState('');
  const [modoDescripcion, setModoDescripcion] = useState(false);
  const [recomendacion, setRecomendacion] = useState('');
  const [loading, setLoading] = useState(true); // Nuevo estado para controlar la carga

  //4.1 **************************************************************
const [input, setInput] = useState('');
//const [peliculasFiltradas, setPeliculasFiltradas] = useState(peliculas);
const [recomendacionIA, setRecomendacionIA] = useState('');
const [peliculasRecomendadas, setPeliculasRecomendadas] = useState([]);
const handleBuscarTexto = () => {
const texto = input.toLowerCase();

const filtradas = peliculas.filter((peli) =>
peli.titulo.toLowerCase().includes(texto) ||
peli.genero.toLowerCase().includes(texto) ||
peli.titulo.toLowerCase().startsWith(texto)
);
setPeliculasFiltradas(filtradas);
setPeliculasRecomendadas([]);
setRecomendacionIA('');
};

const handleBuscarDescripcion = async () => {
setRecomendacionIA('Pensando...');
setPeliculasRecomendadas([]);
setPeliculasFiltradas([]);
try {
const response = await fetch('/api/recomendaciones', {
method: 'POST',
headers: { 'Content-Type': 'application/json' },
body: JSON.stringify({
prompt: `Tengo una base de datos con estas pel√≠culas:
${peliculas.map(p => p.titulo).join(', ')}.
Quiero que me digas solo los t√≠tulos de las pel√≠culas que coincidan con esta
descripci√≥n: "${input}".
Devu√©lveme √∫nicamente los t√≠tulos separados por comas.`
}),
});
const data = await response.json();
const textoIA = data.recomendacion.toLowerCase();
setRecomendacionIA(data.recomendacion);

const coincidencias = peliculas.filter((peli) =>
textoIA.includes(peli.titulo.toLowerCase())
);
setPeliculasRecomendadas(coincidencias);
} catch (err) {
setRecomendacionIA('‚ùå Error al obtener recomendaci√≥n IA.');
}
};
  //**************************************************************************

  // Efecto para cargar las pel√≠culas desde el backend al montar el componente
  useEffect(() => {
    setLoading(true); // Inicia el estado de carga
    // Usamos la URL completa para asegurar la conexi√≥n al backend
    fetch('http://localhost:4000/api/peliculas')
      .then(res => {
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        return res.json();
      })
      .then(data => {
        console.log('Pel√≠culas obtenidas del backend:', data); // Log para depuraci√≥n
        setPeliculas(data);
        setPeliculasFiltradas(data);
      })
      .catch(err => {
        console.error('Error al obtener pel√≠culas:', err); // Log de error
        setPeliculas([]); // En caso de error, asegura que las listas est√©n vac√≠as
        setPeliculasFiltradas([]);
      })
      .finally(() => {
        setLoading(false); // Finaliza el estado de carga
      });
  }, []); // El array de dependencias vac√≠o asegura que se ejecute solo una vez al montar

  // Funci√≥n para manejar la b√∫squeda tradicional por t√≠tulo o g√©nero
  const handleBuscar = (e) => {
    e.preventDefault(); // Previene el comportamiento por defecto del formulario
    const texto = busqueda.toLowerCase();
    const resultado = peliculas.filter(p =>
      p.titulo.toLowerCase().includes(texto) ||
      p.genero.toLowerCase().includes(texto) ||
      p.titulo.toLowerCase().startsWith(texto)
    );
    setPeliculasFiltradas(resultado);
    setRecomendacion(''); // Limpia cualquier recomendaci√≥n previa de IA
  };

  // Funci√≥n para manejar la b√∫squeda por descripci√≥n usando la IA
  const handleBuscarPorDescripcion = async () => {
    try {
      // Realiza una petici√≥n POST al endpoint de recomendaciones del backend
      const res = await fetch('http://localhost:4000/api/recomendaciones', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // Env√≠a el prompt a la IA con la descripci√≥n del usuario y el cat√°logo de pel√≠culas
        body: JSON.stringify({ prompt: `Dame una recomendaci√≥n basada en esta descripci√≥n: ${busqueda}. Usa solo pel√≠culas de este cat√°logo: ${peliculas.map(p => p.titulo).join(', ')}.` })
      });
      const data = await res.json();
      setRecomendacion(data.recomendacion); // Almacena la recomendaci√≥n de la IA

      // Filtra las pel√≠culas que coincidan con la recomendaci√≥n de la IA
      const seleccionadas = peliculas.filter(p =>
        data.recomendacion.toLowerCase().includes(p.titulo.toLowerCase())
      );
      if (seleccionadas.length > 0) {
        setPeliculasFiltradas(seleccionadas); // Actualiza las pel√≠culas filtradas
      } else {
        setPeliculasFiltradas([]); // Si no hay coincidencias, muestra una lista vac√≠a
      }
    } catch (err) {
      console.error('Error con IA:', err); // Manejo de errores en la llamada a la IA
      setRecomendacion('Error al obtener recomendaci√≥n de IA.');
      setPeliculasFiltradas([]);
    }
  };
