<h3>{p.titulo}</h3>
<p>{p.genero}</p>
<span>{p.descripcion?.slice(0, 60)}...</span>
</div>
</div>
))}
</div>
//

return (
<div className="App">
<h1 className="titulo">CECYFLIX</h1>
<div className="buscador">
<input
type="text"
placeholder="Â¿QuÃ© te gustarÃ­a ver hoy?"
